package org.jdelaunay.delaunay;/** * Delaunay Package. *  * @author Jean-Yves MARTIN * @date 2009-01-12 * @version 1.0 */import java.awt.*;import javax.swing.*;public class MyDrawing extends JFrame {	private static final long serialVersionUID = 1L;	protected MyMesh theMesh;	public MyDrawing() {		super("Display Panel");		setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);		setSize(1400, 800);		setVisible(true);					theMesh = null;	}	public void add(MyMesh aMesh) {		theMesh = aMesh;	}		public void paint(Graphics g) {		if (theMesh != null)			theMesh.displayObject(g);	}		public void refresh() {		this.invalidate();		this.repaint();	}}